import React from 'react';
import styled from 'styled-components';

const PendingCardCon = styled.div`
  width: 100%;
  max-width: 900px;
  padding: 20px;
  margin: 20px auto;
  text-align: left;
  color: rgb(124, 128, 129);
`

const ImgCon = styled.div`
  max-width: 20%;
`

const Img = styled.img`
  max-width: 100%;
`

const Name = styled.div`
  text-transform: uppercase;
  font-size: 1.4rem;
  letter-spacing: 2px;
  margin-bottom: 10px;
`

const Country = styled.div`
  text-transform: uppercase;
`

const Story = styled.div`
  letter-spacing: 1px;
  line-height: 1.5;
  margin: 20px auto 10px auto;
`

const PendingContent = styled.div`
  display: flex;
  width: 100%;
`

const PendingText = styled.div`
  padding-left: 2%;
`
const ApprovalButtonsCon = styled.div`
  display: flex;
  justify-content: flex-end;
`



const StoryCardPending = (props) => {

  const onClickApprove = event => {
    event.preventDefault()
    // name will be placeholder for id until backend is ready to implement to project
    // console.log(props.name)
    props.approveStory(props.name)
  }

  const onClickDelete = event => {
    event.preventDefault();
    props.deleteStory(props.name)
  }

  return (
    <PendingCardCon>
      <PendingContent>
        <ImgCon>
          <Img src={props.imgUrl} alt="" />
        </ImgCon>
        <PendingText>
          <Name>{props.name}</Name>
          <div>{props.age}</div>
          <Country>{props.country}</Country>
          <Story>{props.story}
          </Story>
        </PendingText>
      </PendingContent>
      <ApprovalButtonsCon>
        <button onClick={onClickApprove}>Approve</button>
        {/* <button>Edit</button> */}
        <button onClick={onClickDelete}>Delete</button>
      </ApprovalButtonsCon>
    </PendingCardCon>
  )
}

export default StoryCardPending;
